import axios from "axios";

const BASE_URL = "http://localhost:8080/employees"; // Assuming this is the correct endpoint for employees

class EmployeeService {
  // Method to get all employees
  getAllEmployees() {
    return axios.get(BASE_URL);
  }

  // Method to save a new employee
  saveEmployee(employeeData) {
    return axios.post(BASE_URL, employeeData);
  }

  // Method to update an employee
  updateEmployee(id, employeeData) {
    return axios.put(`${BASE_URL}/${id}`, employeeData);
  }

  // Method to get an employee by ID
  getEmployeeById(id) {
    return axios.get(`${BASE_URL}/${id}`);
  }

  // Method to delete an employee
  deleteEmployee(id) {
    return axios.delete(`${BASE_URL}/${id}`);
  }
}

export default new EmployeeService();
