import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import DepartmentService from '../service/DepartmentService';

const ListDepartmentComponent = () => {
    const [departmentArray, setDepartmentArray] = useState([]);

    useEffect(() => {
        getAllDepartment();
    }, []);

    function getAllDepartment() {
        DepartmentService.getAllDepartment()
            .then(res => { setDepartmentArray(res.data); console.log(res) })
            .catch(e => console.log(e));
    }
    function deleteDepartment(e, id) {
        e.preventDefault()
        DepartmentService.deleteDepartment(id).then(getAllDepartment()).catch(e => console.log(e));
    }


    return (
        <div className='container'>
            <Link to={"/add-employee"} className='btn btn-primary mb-2 mt-3' href="">Add Department</Link>
            <h2 className='text-center mb-4'>List Employee</h2>
            <table className='table table-bordered table striped'>
                <thead>
                    <th>Department ID</th>
                    <th>Department Name</th>
                    <th>Department Description </th>
                    <th>Actions</th>
                </thead>
                <tbody>
                    {departmentArray.map(department =>
                        <tr id={department.id}>
                            <td>{department.id}</td>
                            <td>{department.name}</td>
                            <td>{department.description}</td>
                            <td>
                                <Link to={`/add-department/${department.id}`} className='btn btn-info' href="">Update</Link> {" "}
                                <a onClick={(e) => deleteEmployee(e, department.id)} className='btn btn-danger'>Delete</a>
                            </td>
                        </tr>)}

                </tbody>
            </table>
        </div>
    )
}

export default ListDepartmentComponent
