import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import EmployeeService from '../service/EmployeeService';
import DepartmentService from '../service/DepartmentService';

const ListEmployeeComponent = () => {
    const [employeeArray, setEmployeeArray] = useState([]);
    const [departments, setDepartments] = useState({});

    useEffect(() => {
        // Fetch employees and their associated departments
        getAllEmployee();
        getAllDepartments();
    }, []);

    function getAllEmployee() {
        EmployeeService.getAllEmployee()
            .then(res => {
                setEmployeeArray(res.data);
                console.log(res);
            })
            .catch(e => console.log(e));
    }

    function getAllDepartments() {
        DepartmentService.getAllDepartment()
            .then(res => {
                // Create a mapping of department IDs to department objects
                const departmentMap = {};
                res.data.forEach(department => {
                    departmentMap[department.id] = department;
                });
                setDepartments(departmentMap);
            })
            .catch(e => console.log(e));
    }

    function deleteEmployee(e, id) {
        e.preventDefault();
        EmployeeService.deleteEmployee(id)
            .then(() => getAllEmployee())
            .catch(e => console.log(e));
    }

    return (
        <div className='container'>
            <Link to={"/add-employee"} className='btn btn-primary mb-2 mt-3'>
                Add Employee
            </Link>

            <h2 className='text-center mb-4'>List Employee</h2>
            <table className='table table-bordered table-striped'>
                <thead>
                    <tr>
                        <th>Employee ID</th>
                        <th>Employee First Name</th>
                        <th>Employee Last Name</th>
                        <th>Employee Email</th>
                        <th>Department</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {employeeArray.map(employee => (
                        <tr key={employee.id}>
                            <td>{employee.id}</td>
                            <td>{employee.firstName}</td>
                            <td>{employee.lastName}</td>
                            <td>{employee.email}</td>
                            <td>{employee.department.name}</td>
                            <td>
                                <Link
                                    to={`/add-employee/${employee.id}`}
                                    className='btn btn-info'
                                >
                                    Update
                                </Link>{" "}
                                <a
                                    onClick={(e) => deleteEmployee(e, employee.id)}
                                    className='btn btn-danger'
                                >
                                    Delete
                                </a>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ListEmployeeComponent;
